  function createCard(video) {
    const card = document.createElement('div');
    card.className = 'video-card';
  
    const thumbWrapper = document.createElement('div');
    thumbWrapper.className = 'thumbnail-wrapper';
    thumbWrapper.addEventListener('click', () => {
      window.open(`https://www.youtube.com/watch?v=${video.youtubeId}`, '_blank', 'noopener,noreferrer');
    });
  
    const img = document.createElement('img');
    img.src = `https://img.youtube.com/vi/${video.youtubeId}/mqdefault.jpg`;
    img.alt = video.title;
  
    const playBtn = document.createElement('div');
    playBtn.className = 'play-btn';
    // ▼ Replaced the old "▶" character with an SVG triangle
    playBtn.innerHTML = `
      <svg class="play-svg" viewBox="0 0 24 24" aria-hidden="true">
        <polygon points="6 3 20 12 6 21 6 3"></polygon>
      </svg>
    `;
  
    const gradient = document.createElement('div');
    gradient.className = 'gradient-overlay';
  
    thumbWrapper.appendChild(img);
    thumbWrapper.appendChild(playBtn);
    thumbWrapper.appendChild(gradient);
  
    const info = document.createElement('div');
    info.className = 'video-info';
    info.innerHTML = `<h3>${video.title}</h3><p>${video.description || ''}</p>`;
  
    card.appendChild(thumbWrapper);
    card.appendChild(info);
  
    return card;
  }
  
  document.addEventListener('DOMContentLoaded', () => {
    const container = document.getElementById('gallery-container');
    if (!container) return;
    videos.forEach(v => {
      container.appendChild(createCard(v));
    });
  });
  